package patterns;

public class Number3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=5;i>=1;i--)
		{
			for(int j=5;j>=1;j--)
			if(i==1||i==5||j==5||j==1)
				System.out.print("*");
			else
				System.out.print(" ");
				
		System.out.println();

		}
		}



		

}
