package patterns;

public class Number4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=5;
		for (int i=n;i>=1;i--)
		
		{
			for(int j=n;j>=1;j--)
			if(i==1||i==3||i==5)
				System.out.print("1");
			else
				System.out.print("0");
		System.out.println();

		}
		}
	}

